package com.syntax.class02;

public class Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a = "Angelo";
		String b = "Viloan";
		String c = "A";
		String d = "Falls Church";
		String e = "Virginia";
		String f = "123 456 7890";
		
		System.out.println("My name is " + a + " and my last name is " + b);
		System.out.println("I am "+ c + " student");
		System.out.println("I live in city " + d + " and state " + e);
		System.out.println("And my phone number is " + f);
		System.out.println();
		
		d = "Ocean City";
		e = "Maryland";
		f = "098 765 4321";
		
		System.out.print("My name is " + a +" "+ b + " and I moved to new city "+ d +".");
		System.out.println(" and new state " + e);
		System.out.println("My new phone number is " + f);
		
		
	}

}
