package com.syntax.class07;

public class Homework3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20;
		while(a>=1) {
			
			System.out.print(a+ " ");
			a-=2;
		}
		System.out.println("");
		System.out.println("----------------------------------------");
		int b = 20;
		while (b>=1) {
			
			if(b%2==0) {
				System.out.print(b+" ");
			}
			b--;
		}
	}

}
